import React from "react";
import ReactDOM from "react-dom";
import styles from './CartModal.module.css';
import { useSelector } from "react-redux";

const CartModal = () => {

  let cartSelector = useSelector(state => state.cart.items);

  let viewCartItems = cartSelector.map(item => (
    <div className={styles.cart}>
      <img className={styles.img} src={item.image} alt={item.title} />
      <h3 className={styles.cart_title}>{ item.name }</h3>
      <div>
        <h3>{ item.totalPrice }</h3>
        <button className={styles.btn} id={styles.btn_inc}>+</button>
        <p>{}</p>
        <button className={styles.btn} id={styles.btn_dec}>-</button>
      </div>
      <h4>{ item.price }</h4>
    </div>
  ))

  return ReactDOM.createPortal(
    <div className={styles.modal_container}>
      <h2>Cart List</h2>
      { viewCartItems }
    </div>,
    document.getElementById("modal-root")
  );
};

export default CartModal;
